INSERT INTO book VALUES('ISBN1234', 'HTML5 웹 프로그래밍', 29000, '윤인성','이 책은 웹 프로그래밍을 시작하는 입문자를 위한 실습서입니다. 단계별 예제를 따라 하며 HTML5, CSS3, 자바스크립트의 기초를 익히고 jQuery와 실무에서 쓰이는 다양한 플러그인을 경험할 수 있습니다.', 'IT모바일', 1000,  '2023/08/05', 'New', 'ISBN1234.jpg');
INSERT INTO book VALUES('ISBN1235', 'JSP 웹 프로그래밍', 27000, '송미영', ' JSP의 이론적 개념 → 기본 실습 → 응용 실습 순의 단계별 학습이 가능합니다. 응용 실습이 합쳐져 최종적으로 쇼핑몰 하나를 완성하는 구성이라 배운 내용이 어디에 어떻게 적용되는지 알 수 있습니다.', 'IT모바일',1000, '2018/10/08', 'New', 'ISBN1235.jpg');
INSERT INTO book VALUES('ISBN1236', '스프링 프레임워크', 30000, '김명호', '스프링 프레임워크의 전체적인 구조와 핵심 이론을 다양한 예제 프로젝트를 통해 배울 수 있습니다. 전자 도서관 시스템의 여러 기능을 구현하며 스프링 MVC의 심화 개념과 실무 기술도 익힐 수 있습니다.', 'IT모바일', 1000, '2023/07/', 'New', 'ISBN1236.jpg');